// Letcode problem: 120. Triangle
// Letcode link:https://leetcode.com/problems/triangle/
// Level: medium
//Given a triangle array, return the minimum path sum from top to bottom.
//For each step, you may move to an adjacent number of the row below.
//More formally, if you are on index i on the current row, you may move to either index i or index i + 1 on the next row.
package main

import (
	"fmt"
	"sort"
)

//Dynamic + Tabulation (Bottom-Up)
//Runtime: 10 ms, faster than 33.46% of Go online submissions for Triangle.
//Memory Usage: 3.9 MB, less than 22.06% of Go online submissions for Triangle.
func minimumTotalTabulation(triangle [][]int) int {
	var dpPrev []int

	for i := range triangle {
		if i == 0 {
			dpPrev = []int{triangle[0][0]}
		} else {
			var dpNext []int
			for col := range dpPrev {
				sum1 := dpPrev[col] + triangle[i][col]
				sum2 := dpPrev[col] + triangle[i][col+1]
				if col >= len(dpNext) {
					dpNext = append(dpNext, sum1)
				} else {
					dpNext[col] = min(dpNext[col], sum1)
				}

				if col+1 >= len(dpNext) {
					dpNext = append(dpNext, sum2)
				} else {
					dpNext[col+1] = min(dpNext[col+1], sum2)
				}

			}
			dpPrev = dpNext
			fmt.Printf("DP[%d]:%v\n", i, dpNext)
		}

	}

	sort.Ints(dpPrev)

	return dpPrev[0]
}

//------------------------------------------
//Dynamic + Memorization (Top-down)
//Runtime: 0 ms, faster than 100.00% of Go online submissions for Triangle.
//Memory Usage: 3.7 MB, less than 28.68% of Go online submissions for Triangle.
func minimumTotalMemorization(triangle [][]int, n int, dp [][]int) []int {
	fmt.Printf("n:%d\n", n)
	if n == 0 {
		dp[n][0] = triangle[n][0]
	} else {
		dpPrev := minimumTotalMemorization(triangle, n-1, dp)
		fmt.Printf("n:%d dpPrev:%v\n", n, dpPrev)
		dp[n][0] = triangle[n][0] + dpPrev[0]
		for i := 1; i <= n; i++ {
			if i == len(dpPrev) {
				dp[n][i] = triangle[n][i] + dpPrev[i-1]
			} else {
				dp[n][i] = min(triangle[n][i]+dpPrev[i], triangle[n][i]+dpPrev[i-1])
			}
		}
	}
	fmt.Printf("n:%d dp[%d]:%v\n", n, n, dp[n])
	return dp[n]
}
func minimumTotal2(triangle [][]int) int {
	n := len(triangle)
	//Build dp state table
	dp := make([][]int, n)
	for i := range triangle {
		dp[i] = make([]int, len(triangle[i]))
	}
	dp[n-1] = minimumTotalMemorization(triangle, n-1, dp)
	sort.Ints(dp[n-1])
	return dp[n-1][0]
}

//------------------------------------------

//Dynamic + Top-down Memorization
//https://leetcode.com/problems/triangle/discuss/2132006/Top-Down-Memoized-solution-Python-3
func minimumTotalMemorization2(triangle [][]int, row int, col int, dp *[][]int) int {
	rows := len(triangle)
	fmt.Printf("row:%d col:%d rows:%d dp[%d][%d]:%d\n", row, col, rows, row, col, (*dp)[row][col])
	val := triangle[row][col]
	if row == rows-1 {
		return val
	}
	if (*dp)[row][col] != 0 {
		return (*dp)[row][col]
	}
	minPath := val + min(minimumTotalMemorization2(triangle, row+1, col, dp), minimumTotalMemorization2(triangle, row+1, col+1, dp))
	(*dp)[row][col] = minPath
	return minPath
}
func minimumTotal(triangle [][]int) int {
	//Build dp state table
	n := len(triangle)
	dp := make([][]int, n)
	for i := range triangle {
		dp[i] = make([]int, len(triangle[i]))
	}
	return minimumTotalMemorization2(triangle, 0, 0, &dp)
}

